import React, { useState, useContext } from "react";

import MenuContext from "../../Store/menu-context";

import CartItem from "./CartItem";

import Button from "../../UI/Button";
const CartItemList = () => {
  const ctx = useContext(MenuContext);

  //item in cart = {name: , price: , amount: }
  //   const [totalPrice, setTotalPrice] = useState(0);

  // function displayName(){
  //     console.log(props.)
  // }

  const initialSubtotals = ctx.yourCart.map((item) => item.price * item.number)
  const [subtotalList, setSubtotalList] = useState(initialSubtotals);

  // useEffect(() => {
  //   return () => {
  //     setSubtotalList(subtotalList);
  //   }
  // }, [subtotalList] )



  let cartDisplay = <p>No items in cart</p>;

  if (ctx.yourCart.length > 0) {
    cartDisplay = ctx.yourCart.map((item) => (
      <CartItem
        name={item.name}
        price={item.price}
        number={item.number}
        key={item.key}
        setSubtotalList={setSubtotalList}
      />
    ));
  }

  // const subTotals = ctx.yourCart.map((item) => item.price * item.number);
  // ctx.yourCart.forEach((item) => console.log(item.price));
  // ctx.yourCart.forEach((item) => console.log(item.number));
  // ctx.yourCart.forEach((item) => console.log(item.price * item.number));

  const reducer = (a, b) => a + b;
  // console.log(subTotals);
  //   setTotalPrice(subTotals.reduce(reducer))

  let totalPrice;

  if (subtotalList.length > 0) {
    totalPrice = subtotalList.reduce(reducer);
  }

  return (
    <>
      {cartDisplay}
      <footer>
        <p>Total Amount</p>
        <p>{totalPrice.toString()}</p>
        <Button buttonLabel={"Close"} />
        <Button buttonLabel={"Order"} />
      </footer>
    </>
  );
};

export default CartItemList;
