import React, { useContext } from "react";

import MenuContext from "../../Store/menu-context";

import CartItem from "./CartItem";

import Button from "../../UI/Button";
const CartItemList = (props) => {
  const ctx = useContext(MenuContext);

  //item in cart = {name: , price: , amount: }
  const totalPrice = 0;

  // function displayName(){
  //     console.log(props.)
  // }

  let cartDisplay = <p>No items in cart</p>;

  if (ctx.yourCart.Length > 0) {
    cartDisplay = ctx.yourCart.map((item) => (
      <CartItem name={item.name} price={item.price} />
    ));
  }

  return (
    <>
      {/* {ctx.yourCart &&
        ctx.yourCart.map((item) => (
          <CartItem name={item.name} price={item.price} />
        ))} */}
        {cartDisplay}
      <footer>
        <p>Total Amount</p>
        <p>{totalPrice}</p>
        <Button buttonLabel={"Close"} buttonFunction={console.log(ctx.yourCart.length)} />
        <Button buttonLabel={"Order"} />
      </footer>
    </>
  );
};

export default CartItemList;
