import React, { useState, useContext } from "react";

import Button from "../../UI/Button";

import MenuContext from "../../Store/menu-context";

const CartItem = (props) => {
  // const [number, setNumber] = useState(props.number);

  // const subtotal = props.price * number;
  // console.log(subtotal)

  // // props.setSubtotalList(subtotal);

  // function addItem() {
  //   setNumber(Number(number) + 1);
  //   // setSubtotal(props.price * number);
  //   props.setSubtotalList(subtotal);
  // };

  // function decreaseItem(){
  //   if (number === 0){
  //     console.log(`You can't have negative food`);
  //   } else{
  //   setNumber(Number(number) - 1);
  //   // setSubtotal(props.price * number);
  //   props.setSubtotalList(subtotal);
  //   }
  // };
  const [number, setNumber] = useState(props.number);

  const ctx = useContext(MenuContext);

  const subtotals = ctx.yourCart.map((item) => item.price * item.number);

  const reducer = (a, b) => a + b;
  const totals = subtotals.reduce(reducer);

  function incrementItem() {
    ctx.yourCart.forEach((item) => {
      if (item.name === props.name) {
        props.setItemNumber(props.name, (item.number = item.number + 1));
        setNumber(number + 1);
        props.setTotalPrice(totals + item.price);
      }
    });
  }

  function decrementItem() {
    if (number > 0) {
      ctx.yourCart.forEach((item) => {
        if (item.name === props.name) {
          props.setItemNumber(props.name, (item.number = item.number - 1));
          setNumber(number - 1);
          props.setTotalPrice(totals - item.price);
        }
      });
    }
  }

  // const [cartState, dispatchCart] = useReducer(cartReducer, {ctx.yourCart});

  return (
    <li>
      <h3>{props.name}</h3>
      <p>{props.price}</p>
      <p>{number}</p>
      <Button buttonLabel="+" buttonFunction={incrementItem} />
      <Button buttonLabel="-" buttonFunction={decrementItem} />
    </li>
  );
};

export default CartItem;
