import React, { useState, useContext, useEffect } from "react";

import Button from "../UI/Button";
import MenuContext from "../Store/menu-context";

const MenuItem = (props) => {
  const [amount, setAmount] = useState(0);
  // const fullPrice = props.price * amount;
  const item = { name: props.name, price: props.price, amount: amount };

  const ctx = useContext(MenuContext);

  function amountChangeHandler(event) {
    setAmount(event.target.value);
  }

  function addToCart() {
    if (amount > 0) {
      ctx.addItem(item);
      console.log("item added");
    } else {
      console.log("There was an error");
    }
  }

  useEffect(() => {
    const timer = setTimeout(() => {
      console.log(ctx.yourCart);
    }, 3000);
    return () => {
      clearTimeout(timer);
    };
  }, [ctxyourCart]);

  return (
    <li>
      <p>{props.name}</p>
      <p>{props.price}</p>

      <div>
        <label> Amount</label>
        <input onChange={amountChangeHandler} type="number"></input>
        <Button buttonFunction={addToCart} buttonLabel="+ Add" />
      </div>
    </li>
  );
};

export default MenuItem;
