import React, { useState } from 'react';

import Button from '../../UI/Button';

const CartItem = (props) => {

const [number, setNumber] = useState(props.number);

const [subtotal, setSubtotal] = useState(props.price * number);

props.setSubtotalList(subtotal);

function addItem() {
  setNumber(Number(number) + 1);
  setSubtotal(props.price * number);
  props.setSubtotalList(subtotal);
};

function decreaseItem(){
  if (number === 0){
    console.log(`You can't have negative food`);
  } else{
  setNumber(Number(number) - 1);
  setSubtotal(props.price * number);
  props.setSubtotalList(subtotal);
  }
};

  return (
    <li>
      <h3>{props.name}</h3>
      <p>{props.price}</p>
      <p>{number}</p>
      <Button buttonLabel='+' buttonFunction={addItem} />
      <Button buttonLabel='-' buttonFunction={decreaseItem}/>
    </li>
  );
};

export default CartItem;
