import React, { useState, useContext } from "react";

import Button from "../UI/Button";
import MenuContext from "../Store/menu-context";

const MenuItem = (props) => {
  const [number, setNumber] = useState(0);
  // const fullPrice = props.price * amount;
  const item = {
    name: props.name,
    price: props.price,
    number: number,
    key: props.id,
  };

  const ctx = useContext(MenuContext);

  function numberChangeHandler(event) {
    setNumber(event.target.value);
  }

  // const timer = setTimeout(() => {
  //   console.log(ctx.yourCart);
  // }, 3000);

  function addToCart() {
    if (number > 0 && ctx.yourCart.includes(item.name)) {
      ctx.yourCart.forEach((cartItem) => {
        if (cartItem.name === item.name) {
          cartItem.number += number;
        }
      });
    } 

    else if (number > 0) {
      ctx.addItem(item);
      console.log("item added");
      // clearTimeout(timer);
    } else {
      console.log("There was an error");
    }
  }

  // useEffect(() => {
  //   const timer = setTimeout(() => {
  //     console.log(ctx.yourCart);
  //   }, 3000);
  //   return () => {
  //     clearTimeout(timer);
  //   };
  // }, [ctx.yourCart]);

  return (
    <li>
      <p>{props.name}</p>
      <p>{props.price}</p>

      <div>
        <label> Amount</label>
        <input onChange={numberChangeHandler} type="number"></input>
        <Button buttonFunction={addToCart} buttonLabel="+ Add" />
      </div>
    </li>
  );
};

export default MenuItem;
